const slideImages = [
  'images/1.jpg',
  'images/2.jpg',
  'images/3.jpg',
  'images/4.jpg',
  'images/5.jpg',
  'images/6.jpg',
  'images/7.jpg'
];

// image slide url css
const useStyles = makeStyles({
  img1: {
    backgroundImage: `url(${slideImages[0]})`
  },
  img2: {
    backgroundImage: `url(${slideImages[1]})`
